import React, {Fragment, useState, useEffect} from "react";
import {useMutation, useQuery} from "@apollo/react-hooks";
import {useHistory, useLocation} from 'react-router-dom'
import gql from "graphql-tag";
import {Button, Card, Row, Col, TextInput, Preloader, Collection, CollectionItem} from 'react-materialize'
import Grid from "@material-ui/core/Grid";
import {CardActions, CardContent, CardHeader, TextField} from "@material-ui/core";
import Typography from "@material-ui/core/Typography";
import PublishIcon from "@material-ui/icons/Publish";
import {makeStyles} from "@material-ui/core/styles";

const useStyles = makeStyles(theme => ({


    textWidth: {
        '& .MuiFormControl-root': {
            width: "100%"
        }
    },

    cardTitle: {
        textAlign: "center"
    },

}));

const VERIFY = gql`
    {
        verify 
    }
`;

const LOGIN = gql`
    mutation login($email: String! $password: String!) {
        login(email: $email, password: $password)
    }
`;

const useQueryParam = () => {
    return new URLSearchParams(useLocation().search);
};

const Login = () => {
    const classes = useStyles();
    // setting the states
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [verified, setVerified] = useState(null);

    const query = useQueryParam();
    const redirect = query.get('redirect') || '/';
    const history = useHistory();

    const {loading, error, data} = useQuery(VERIFY, {
        onCompleted(verified) {
            setVerified(verified);
            window.location.assign(redirect);
        }
    });


    const [login] = useMutation(LOGIN, {
        onCompleted(result) {
            setVerified(true);
            window.location.assign(redirect);
        },

        onError(error) {

            setVerified(false)
        }
    });

    const pageTitle =
        <Row>
            <Col s={12} className={"white-text"}>
                <h2>Login</h2>
            </Col>
        </Row>;

    return (
        <Fragment>
            {pageTitle}
            {loading ? <Preloader active color="white" size="big"/> :
                <Grid
                    container
                    spacing={0}
                    direction={"row"}
                    justify={"center"}
                    alignItems={"center"}
                    className={classes.textWidth}
                >
                    <Grid item xs={4}>
                        <form
                            onSubmit={e => {
                                e.preventDefault();

                                login({
                                    variables: {
                                        email,
                                        password
                                    }
                                }).catch(e => {
                                    console.log(e);
                                })
                            }}
                            id={"cred-form"}
                        >
                            <Card className={classes.cardPadding}>
                                <CardHeader
                                    title={<Typography variant={"h4"} className={classes.cardTitle}>Sign up</Typography>}>

                                </CardHeader>
                                <CardContent>
                                        <TextField
                                            label={"Email address"}
                                            value={email}
                                            type={"email"}
                                            onChange={(e) => {
                                                setEmail(e.target.value)
                                            }}
                                        />

                                        <TextField
                                            label={"Password"}
                                            value={password}
                                            type={"password"}
                                            onChange={(e) => {
                                                setPassword(e.target.value)
                                            }}
                                        />
                                </CardContent>
                                <CardActions style={{justifyContent: "flex-end"}}>
                                    <Button color="primary" variant="contained" endIcon={<PublishIcon/>}>Submit</Button>
                                </CardActions>
                            </Card>
                        </form>
                    </Grid>
                </Grid>
            }
        </Fragment>
    )
};

export default Login;
