import React, {Fragment, useState} from "react";
import {useMutation} from "@apollo/react-hooks";
import gql from "graphql-tag";
import {Button, Card, CardContent, CardHeader, FormGroup, Grid, TextField} from '@material-ui/core'
import {makeStyles} from "@material-ui/core/styles";
import Typography from "@material-ui/core/Typography";
import FormControl from "@material-ui/core/FormControl";

const SIGNUP = gql`
    mutation signup($first_name: String $last_name: String $email: String! $password: String!) {
        signup(input: {first_name: $first_name, last_name: $last_name, email: $email, password: $password})
    }
`;

const useStyles = makeStyles({
    cardTitle: {
        textAlign: "center"
    }
});

export default function SignUp() {
    document.title = "Auth - Sign up";
    const classes = useStyles();
    // setting the states
    const [firstName, setFirstName] = useState("");
    const [lastName, setLastName] = useState("");
    const [username, setUsername] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [password2, setPassword2] = useState("");
    const [validPass, setValidPass] = useState(true);
    const [success, setSuccess] = useState(null);

    const [signup] = useMutation(SIGNUP, {
        onCompleted(result) {
            setSuccess(true)
        },

        onError(error) {

            setSuccess(false)
        }
    });

    const passwordsMatch = (toMatch) => {
        setValidPass(toMatch === password);

        setPassword2(toMatch);
    };

    return (
        <Fragment>
            <Grid
                container
                spacing={0}
                direction={"row"}
                justify={"center"}
                alignItems={"center"}
            >
                <Grid  item xs={6}>
                    <form
                        onSubmit={e => {
                            e.preventDefault();

                            if (!validPass) {
                                return false;
                            }

                            signup({
                                variables: {
                                    first_name: firstName,
                                    last_name: lastName,
                                    email,
                                    password
                                }
                            }).catch(e => {
                                console.log(e);
                            })
                        }}
                        id={"cred-form"}
                    >
                        <Card>
                            <CardHeader
                                title={<Typography variant={"h4"} className={classes.cardTitle}>Sign up</Typography>}>

                            </CardHeader>
                            <CardContent>

                                <Grid item container direction={"column"}>
                                    <Grid item container direction={"row"} xs={12} justify={"space-evenly"}>
                                        <FormControl>
                                            <TextField
                                                label={"First Name"}
                                                value={firstName}
                                                onChange={(e) => {
                                                    setFirstName(e.target.value)
                                                }}
                                            />
                                        </FormControl>
                                        <FormControl>
                                            <TextField
                                                label={"Last Name"}
                                                value={lastName}
                                                onChange={(e) => {
                                                    setLastName(e.target.value)
                                                }}
                                            />
                                        </FormControl>
                                    </Grid>=



                                </Grid>

                            </CardContent>
                        </Card>
                    </form>
                </Grid>
            </Grid>
        </Fragment>
    )
}
