import React, {Fragment, useState, useEffect} from "react";
import {useMutation} from "@apollo/react-hooks";
import {useHistory, useLocation} from 'react-router-dom'
import gql from "graphql-tag";
import {Button, Card, Row, Col, TextInput} from 'react-materialize'

const LOGIN = gql`
    mutation login($email: String! $password: String!) {
        login(email: $email, password: $password)
    }
`;

const CHECK_USER = gql`    
    query verify {
        verify
    }
`;

const useQuery = () => {
    return new URLSearchParams(useLocation().search);
};

const Login = () => {
    // setting the states
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [success, setSuccess] = useState(null);
    const [userData, setUserData] = useState({});

    const query = useQuery();
    const history = useHistory();

    const [login] = useMutation(LOGIN, {
        onCompleted(result) {
            setSuccess(true);
            const redirect = query.get('redirect');
            window.location.assign(redirect);
        },

        onError(error) {

            setSuccess(false)
        }
    });



    return (
        <div className={"container center-align"}>
            <Row>
                <Col s={12} className={"white-text"}>
                    <h2>Sign up</h2>
                </Col>
            </Row>
            <div className={"valign-wrapper"}>
                <Row>
                    <Col s={12}>
                        <Card>
                            <form onSubmit={e => {
                                e.preventDefault();

                                login({
                                    variables: {
                                        email,
                                        password
                                    }
                                }).catch(e => {
                                    console.log(e);
                                })

                            }} id={"cred-form"}>
                                <Row>
                                    <TextInput s={12} label={"Email"} email error={"Invalid email"} validate
                                               value={email}
                                               onChange={(e) => {
                                                   setEmail(e.target.value);
                                               }} required/>
                                </Row>
                                <Row>
                                    <TextInput s={12} label={"Password"} password value={password} onChange={(e) => {
                                        setPassword(e.target.value);
                                    }} required/>
                                </Row>
                                {
                                    success !== null &&
                                    <Row>
                                        <p className={success ? "green-text darken-3" : "red-text darken-red"}>
                                            {success ? "Login success! Redirecting..." : "Unable to login with provided credentials"}
                                        </p>
                                    </Row>
                                }
                                <Row>
                                    <Button type={"submit"}>Submit</Button>
                                </Row>
                            </form>
                        </Card>
                    </Col>
                </Row>
            </div>
        </div>
    )
};

export default Login;
