import React, {Fragment, useState} from "react";
import {useMutation} from "@apollo/react-hooks";
import gql from "graphql-tag";
import {Button, Card, FormGroup, Grid, TextField} from '@material-ui/core'
import Typography from "@material-ui/core/Typography";

const SIGNUP = gql`
    mutation signup($first_name: String $last_name: String $email: String! $password: String!) {
        signup(input: {first_name: $first_name, last_name: $last_name, email: $email, password: $password})
    }
`;

export default function SignUp() {
    // setting the states
    const [firstName, setFirstName] = useState("");
    const [lastName, setLastName] = useState("");
    const [username, setUsername] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [password2, setPassword2] = useState("");
    const [validPass, setValidPass] = useState(true);
    const [success, setSuccess] = useState(null);

    const [signup] = useMutation(SIGNUP, {
        onCompleted(result) {
            setSuccess(true)
        },

        onError(error) {

            setSuccess(false)
        }
    });

    const passwordsMatch = (toMatch) => {
        setValidPass(toMatch === password);

        setPassword2(toMatch);
    };

    return (
        <Fragment>
            <Grid
                container={4}
                justify={"center"}
            >
                <Grid xs={6}>
                    <Card>
                        <form onSubmit={e => {
                            e.preventDefault();

                            if (!validPass) {
                                return false;
                            }

                            signup({
                                variables: {
                                    first_name: firstName,
                                    last_name: lastName,
                                    email,
                                    password
                                }
                            }).catch(e => {
                                console.log(e);
                            })
                        }} id={"cred-form"}>
                            <Grid container>
                                <Grid>
                                    <FormGroup>
                                        <TextField value={firstName} onChange={(e) => {setFirstName(e.target.value)} }  />
                                    </FormGroup>
                                </Grid>


                            </Grid>
                        </form>
                    </Card>
                </Grid>
            </Grid>
        </Fragment>
    )
}
