import React, {Fragment, useState} from "react";
import {useMutation} from "@apollo/react-hooks";
import gql from "graphql-tag";
import {Button, Card, CardActions, CardContent, CardHeader, FormGroup, Grid, TextField} from '@material-ui/core'
import {makeStyles} from "@material-ui/core/styles";
import Typography from "@material-ui/core/Typography";
import FormControl from "@material-ui/core/FormControl";
import PublishIcon from '@material-ui/icons/Publish';

const SIGNUP = gql`
    mutation signup($first_name: String $last_name: String $email: String! $password: String!) {
        signup(input: {first_name: $first_name, last_name: $last_name, email: $email, password: $password})
    }
`;

const useStyles = makeStyles(theme => ({
    cardPadding: {
        padding: 5
    },

    textWidth: {
        '& .MuiFormControl-root': {
            width: "100%"
        }
    },

    cardTitle: {
        textAlign: "center"
    },

}));

export default function SignUp() {
    document.title = "Auth - Sign up";
    const classes = useStyles();
    // setting the states
    const [firstName, setFirstName] = useState("");
    const [lastName, setLastName] = useState("");
    const [username, setUsername] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [password2, setPassword2] = useState("");
    const [validPass, setValidPass] = useState(true);
    const [success, setSuccess] = useState(null);

    const [signup] = useMutation(SIGNUP, {
        onCompleted(result) {
            setSuccess(true)
        },

        onError(error) {

            setSuccess(false)
        }
    });

    const passwordsMatch = (toMatch) => {
        setValidPass(toMatch === password);

        setPassword2(toMatch);
    };

    return (
        <Fragment>
            <Grid
                container
                spacing={0}
                direction={"row"}
                justify={"center"}
                alignItems={"center"}
                className={classes.textWidth}
            >
                <Grid item xs={6}>
                    <form
                        onSubmit={e => {
                            e.preventDefault();

                            if (!validPass) {
                                return false;
                            }

                            signup({
                                variables: {
                                    first_name: firstName,
                                    last_name: lastName,
                                    email,
                                    password
                                }
                            }).catch(e => {
                                console.log(e);
                            })
                        }}
                        id={"cred-form"}
                    >
                        <Card className={classes.cardPadding}>
                            <CardHeader
                                title={<Typography variant={"h4"} className={classes.cardTitle}>Sign up</Typography>}>

                            </CardHeader>
                            <CardContent>

                                <Grid item container direction={"column"}>
                                    <Grid item container direction={"row"}
                                          spacing={2}
                                          xs={12}>
                                        <Grid item xs={6}>
                                            <TextField
                                                label={"First name"}
                                                value={firstName}
                                                onChange={(e) => {
                                                    setFirstName(e.target.value)
                                                }}
                                            />
                                        </Grid>
                                        <Grid item xs={6}>
                                            <TextField
                                                label={"Last name"}
                                                value={lastName}
                                                onChange={(e) => {
                                                    setLastName(e.target.value)
                                                }}
                                            />
                                        </Grid>
                                    </Grid>

                                    <TextField
                                        label={"Email address"}
                                        value={email}
                                        type={"email"}
                                        onChange={(e) => {
                                            setEmail(e.target.value)
                                        }}
                                    />

                                    <TextField
                                        label={"Password"}
                                        value={password}
                                        type={"password"}
                                        onChange={(e) => {
                                            setPassword(e.target.value)
                                        }}/>
                                    <TextField
                                        label={"Confirm password"}
                                        value={password2}
                                        type={"password"}
                                        onChange={(e) => {
                                            setPassword2(e.target.value)
                                        }}
                                    />

                                </Grid>
                            </CardContent>
                            <CardActions styles={{justify: "right"}}>
                                <Button color="primary" variant="contained" endIcon={<PublishIcon/>}>Submit</Button>
                            </CardActions>
                        </Card>
                    </form>
                </Grid>
            </Grid>
        </Fragment>
    )
}
