import React, {Fragment, useState} from "react";
import {Button, Card, Row, Col, TextInput} from 'react-materialize'

export default function SignUp() {
    // setting the states
    const [firstName, setFirstName] = useState("");
    const [lastName, setLastName] = useState("");
    const [username, setUsername] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [password2, setPassword2] = useState("");



    return (
        <div className={"container valign-wrapper"}>
            <Row>
                <Col s={12}>
                    <Card title={"Sign up"}>
                        <form id={"cred-form"}>
                            <Row>
                                    <TextInput s={6} label={"First Name"} value={firstName} onChange={(e) => {
                                        setFirstName(e.target.value);
                                    }} validate/>
                                    <TextInput s={6} label={"First Name"} value={lastName} onChange={(e) => {
                                        setLastName(e.target.value);
                                    }} validate/>
                            </Row>
                            <Row>
                                <TextInput s={12} label={"Username"} value={username} onChange={(e) => {
                                    setUsername(e.target.value);}} required/>
                            </Row>
                            <Row>
                                <TextInput s={12} label={"Email"} type={"email"} value={email} onChange={(e) => {
                                    setEmail(e.target.value);}} required/>
                            </Row>
                            <Row>
                                <TextInput s={6} label={"Password"} type={"password"} value={password} onChange={(e) => {
                                    setPassword(e.target.value);}} required/>
                                <TextInput s={6} label={"Password"} type={"password"} error={"Passwords do not match"} value={password2} onChange={(e) => {
                                    setPassword2(e.target.value);}} required/>

                            </Row>
                        </form>
                    </Card>
                </Col>

            </Row>
        </div>
    )
}
