import React, {Fragment, useState, useEffect} from "react";
import {useMutation, useQuery} from "@apollo/react-hooks";
import {useHistory, useLocation} from 'react-router-dom'
import gql from "graphql-tag";
import {Button, Card, Row, Col, TextInput, Preloader, Collection, CollectionItem} from 'react-materialize'

const VERIFY = gql`
    {
        verify 
    }
`;

const LOGIN = gql`
    mutation login($email: String! $password: String!) {
        login(email: $email, password: $password)
    }
`;

const useQueryParam = () => {
    return new URLSearchParams(useLocation().search);
};

const Login = () => {
    // setting the states
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [verified, setVerified] = useState(null);

    const query = useQueryParam();
    const redirect = query.get('redirect') || '/';
    const history = useHistory();

    const {loading, error, data} = useQuery(VERIFY, {
        onCompleted(verified) {
            setVerified(verified);
            window.location.assign(redirect);
        }
    });


    const [login] = useMutation(LOGIN, {
        onCompleted(result) {
            setVerified(true);
            window.location.assign(redirect);
        },

        onError(error) {

            setVerified(false)
        }
    });

    const pageTitle =
        <Row>
            <Col s={12} className={"white-text"}>
                <h2>Login</h2>
            </Col>
        </Row>;

    return (
        <Fragment>
            {pageTitle}
            {loading ? <Preloader active color="white" size="big"/> :
                <div className={"valign-wrapper"}>
                    <Row>
                        <Col s={12}>
                            <Card>

                                <form onSubmit={e => {
                                    e.preventDefault();

                                    login({
                                        variables: {
                                            email,
                                            password
                                        }
                                    }).catch(e => {
                                        console.log(e);
                                    })

                                }} id={"cred-form"}>
                                    <Row>
                                        <TextInput s={12} label={"Email"} email error={"Invalid email"} validate
                                                   value={email}
                                                   onChange={(e) => {
                                                       setEmail(e.target.value);
                                                   }} required/>
                                    </Row>
                                    <Row>
                                        <TextInput s={12} label={"Password"} password value={password}
                                                   onChange={(e) => {
                                                       setPassword(e.target.value);
                                                   }} required/>
                                    </Row>
                                    {
                                        verified !== null &&
                                        <Row>
                                            <p className={verified ? "green-text darken-3" : "red-text darken-red"}>
                                                {verified ? "Login success! Redirecting..." : "Unable to login with provided credentials"}
                                            </p>
                                        </Row>
                                    }
                                    <Row>
                                        <Button type={"submit"}>Submit</Button>
                                    </Row>
                                </form>
                            </Card>
                        </Col>
                    </Row>
                </div>
            }
        </Fragment>
    )
};

export default Login;
