import React, {Fragment, useState} from "react";
import {useMutation} from "@apollo/react-hooks";
import gql from "graphql-tag";
import "../styles/sass/auth.sass"
import {
    Button,
    Card,
    CardActions,
    CardContent,
    Grid,
    Hidden,
    Icon,
    Link as MuiLink,
    Slide,
    TextField, useMediaQuery, useTheme
} from '@material-ui/core'
import {makeStyles} from "@material-ui/core/styles";
import Typography from "@material-ui/core/Typography";
import {Link} from "react-router-dom";
import CardMedia from "@material-ui/core/CardMedia";

const SIGNUP = gql`
    mutation signup($first_name: String $last_name: String $email: String! $password: String!) {
        signup(input: {first_name: $first_name, last_name: $last_name, email: $email, password: $password})
    }
`;

const useStyles = makeStyles(theme => ({


    textWidth: {
        '& .MuiFormControl-root': {
            width: "100%"
        }
    },

    cardTitle: {
        textAlign: "center"
    },

}));

export default function SignUp() {
    document.title = "Auth - Sign up";
    const classes = useStyles();
    // setting the states
    const theme = useTheme();
    const match = useMediaQuery(theme.breakpoints.down( 'sm'));

    const [firstName, setFirstName] = useState("");
    const [lastName, setLastName] = useState("");
    const [username, setUsername] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [password2, setPassword2] = useState("");
    const [validPass, setValidPass] = useState(true);
    const [success, setSuccess] = useState(null);

    const [signup] = useMutation(SIGNUP, {
        onCompleted(result) {
            setSuccess(true)
        },

        onError(error) {

            setSuccess(false)
        }
    });

    const passwordsMatch = (toMatch) => {
        setValidPass(toMatch === password);

        setPassword2(toMatch);
    };

    const signinButton =
        <MuiLink to={"/login"} component={Link} underline={"none"}>
        <Button color={"primary"} variant={"contained"}>
            Sign in
        </Button>
    </MuiLink>;

    return (
        <Fragment>
            <Card>
                <Grid container direction={"row"}>
                    <Grid
                        item
                        direction={"column"}
                        justify={"center"}
                        alignItems={"center"}
                        container
                        xs={match ? 12 : 6}
                    >
                        <form
                            onSubmit={e => {
                                e.preventDefault();

                                if (!validPass) {
                                    return false;
                                }

                                signup({
                                    variables: {
                                        first_name: firstName,
                                        last_name: lastName,
                                        email,
                                        password
                                    }
                                }).catch(e => {
                                    console.log(e);
                                })
                            }}
                            id={"cred-form"}
                        >
                            <CardContent>
                                <Typography variant={"h4"} className={classes.cardTitle}>Sign up</Typography>
                                <Hidden smUp>
                                    <Typography variant={"h4"} className={classes.cardTitle}>Or</Typography>
                                    <Grid item align={"center"}>
                                        {signinButton}
                                    </Grid>
                                </Hidden>


                                <TextField
                                    label={"First name"}
                                    value={firstName}
                                    onChange={(e) => {
                                        setFirstName(e.target.value)
                                    }}
                                />

                                <TextField
                                    label={"Last name"}
                                    value={lastName}
                                    onChange={(e) => {
                                        setLastName(e.target.value)
                                    }}
                                />

                                <TextField
                                    label={"Email address"}
                                    value={email}
                                    type={"email"}
                                    onChange={(e) => {
                                        setEmail(e.target.value)
                                    }}
                                />

                                <TextField
                                    label={"Password"}
                                    value={password}
                                    type={"password"}
                                    onChange={(e) => {
                                        setPassword(e.target.value)
                                    }}/>
                                <TextField
                                    label={"Confirm password"}
                                    value={password2}
                                    type={"password"}
                                    onChange={(e) => {
                                        setPassword2(e.target.value)
                                    }}
                                />

                            </CardContent>

                            <CardActions>
                                <Button color="secondary" variant="contained"
                                        type={"submit"}
                                        endIcon={<Icon>send</Icon>}>Submit</Button>
                            </CardActions>

                        </form>
                    </Grid>
                    <Hidden implementation="css" smUp>
                        <Grid
                            item
                            direction={"column"}
                            justify={"center"}
                            alignItems={"center"}
                            container
                            xs={6}
                            className={"welcome"}>
                            <CardContent>
                                <Grid
                                    item
                                    align={"center"}
                                >
                                    <Typography variant="h4">
                                        Welcome
                                    </Typography>

                                    <Typography>
                                        Sign up for access
                                    </Typography>

                                    <Typography>
                                        Or
                                    </Typography>
                                </Grid>

                                <Grid item align={"center"}>
                                    <MuiLink to={"/login"} component={Link} underline={"none"}>
                                        <Button color={"primary"} variant={"contained"}>
                                            Sign in
                                        </Button>
                                    </MuiLink>
                                </Grid>


                            </CardContent>
                        </Grid>
                    </Hidden>

                </Grid>
            </Card>

        </Fragment>
    )
}
