import React, {Fragment, useState} from "react";
import {useMutation} from "@apollo/react-hooks";
import gql from "graphql-tag";
import "../styles/sass/auth.sass"
import {Button, Card, CardActions, CardContent, Grid, Icon, TextField} from '@material-ui/core'
import {makeStyles} from "@material-ui/core/styles";
import Typography from "@material-ui/core/Typography";

const SIGNUP = gql`
    mutation signup($first_name: String $last_name: String $email: String! $password: String!) {
        signup(input: {first_name: $first_name, last_name: $last_name, email: $email, password: $password})
    }
`;

const useStyles = makeStyles(theme => ({


    textWidth: {
        '& .MuiFormControl-root': {
            width: "100%"
        }
    },

    cardTitle: {
        textAlign: "center"
    },

}));

export default function SignUp() {
    document.title = "Auth - Sign up";
    const classes = useStyles();
    // setting the states
    const [firstName, setFirstName] = useState("");
    const [lastName, setLastName] = useState("");
    const [username, setUsername] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [password2, setPassword2] = useState("");
    const [validPass, setValidPass] = useState(true);
    const [success, setSuccess] = useState(null);

    const [signup] = useMutation(SIGNUP, {
        onCompleted(result) {
            setSuccess(true)
        },

        onError(error) {

            setSuccess(false)
        }
    });

    const passwordsMatch = (toMatch) => {
        setValidPass(toMatch === password);

        setPassword2(toMatch);
    };

    return (
        <Fragment>
            <Grid container direction={"row"}>
                <Grid
                    item
                    xs={6}
                    direction={"column"}
                >
                    <form
                        onSubmit={e => {
                            e.preventDefault();

                            if (!validPass) {
                                return false;
                            }

                            signup({
                                variables: {
                                    first_name: firstName,
                                    last_name: lastName,
                                    email,
                                    password
                                }
                            }).catch(e => {
                                console.log(e);
                            })
                        }}
                        id={"cred-form"}
                    >
                        <CardContent>
                            <Typography variant={"h4"} className={classes.cardTitle}>Sign up</Typography>

                            <TextField
                                label={"First name"}
                                value={firstName}
                                onChange={(e) => {
                                    setFirstName(e.target.value)
                                }}
                            />

                            <TextField
                                label={"Last name"}
                                value={lastName}
                                onChange={(e) => {
                                    setLastName(e.target.value)
                                }}
                            />

                            <TextField
                                label={"Email address"}
                                value={email}
                                type={"email"}
                                onChange={(e) => {
                                    setEmail(e.target.value)
                                }}
                            />

                            <TextField
                                label={"Password"}
                                value={password}
                                type={"password"}
                                onChange={(e) => {
                                    setPassword(e.target.value)
                                }}/>
                            <TextField
                                label={"Confirm password"}
                                value={password2}
                                type={"password"}
                                onChange={(e) => {
                                    setPassword2(e.target.value)
                                }}
                            />

                        </CardContent>
                        <CardActions>
                            <Button color="secondary" variant="contained"
                                    endIcon={<Icon>send</Icon>}>Submit</Button>
                        </CardActions>
                    </form>

                </Grid>
                <Grid item xs={6} direction={"column"} align={"center"}>
                    <CardContent className={"welcome"}>
                        <Typography variant="h4">
                            Welcome
                        </Typography>

                        <Typography>
                            Sign up for access
                        </Typography>

                        <Typography>
                            Or
                        </Typography>

                    </CardContent>
                </Grid>
            </Grid>
            <Card>
            </Card>

        </Fragment>
    )
}
